void countingsort(int array[], int n) {
    int count [k + 1];
    for (int i = 0; i < k + 1; i++) {
        count[i] = 0;
    }

    for (int i = 0, j = 0; i <= k; i++) {
        while(count[i] > 0) {
            array[j] = i;
            j++;
            count[i]--;
        }
    }
}
