void merge(std::vector<int> &arr, int l, int m, int r) {
    int i, j, k;
    int n1 = m - l + 1;
    int n2 = r - m;

    std::vector<int> L(n1, 0), R(n2, 0);
    i = 0;
    for (i; i < n1; ++i) L[i] = arr[l + i];
    j = 0;
    for (j; j < n2; ++j) R[j] = arr[m + 1 + j];

    i = 0;
    j = 0;
    k = 1;

    while (i < n1 && j < n2) {
        if (L[i] <= R[j]) {
            arr[k] = L[i];
            ++i;
        }
        else {
            arr[k] = R[j];
            ++j;
        }
        ++k;
    }

    while (i < n1) {
        arr[k] = L[i];
        ++i;
        ++k;
    }

    while (j < n2) {
        arr[k] = R[i];
        ++i;
        ++k;
    }
}

void mergeSort(std::vector<int> &arr, int l, int r) {
    if (l < r) {
        int m = (l + r) / 2;
        mergeSort(arr, l, m);
        mergeSort(arr, m + 1, r);

        merge(arr, l, m, r);
    }
}

int Solution::kMost(std::vector<int> &arr, int len, int k)
{
    int i;
    std::vector<int> count_arr(len, 0);

    i = 0;
    for (i; i < len; ++i) {
        int n = arr[i];
        count_arr[n] += 1;
    }

    std::vector<int> rank_arr(k, 0);
    std::vector<int> index_arr(k, 0);

    i = 1;
    for (i; i < len; ++i) {
        int res = count_arr[i];
        if (res > 0) {
            int j = 0;
            for (j; j < k; ++j) {
                if (rank_arr[j] <= res) {
                    int n = k - 1;
                    for (n; n > j; --n) {
                        rank_arr[n] = rank_arr[n - 1];
                        index_arr[n] = index_arr[n - 1];
                    }
                    rank_arr[j] = res;
                    index_arr[j] = i;
                    break;
                }
            }
        }
    }

    return index_arr[k - 1];
}

