    vector<int> bucket[bucket_number];

    for (int i = 0; i < n; i++) {
        int b = array[i] / ceil(k / bucket_number);
        bucket[b].push_back(array[i]);
    }

    if (bucket_number < k) {
        for (int i = 0; i < bucket_number; i++) {
            sort(bucket[i].begin(), bucket[i].end());
